// Code-behind file (e.g., .aspx.cs)\nprotected void Page_Load(object sender, EventArgs e)\n{\n    if (!IsPostBack)\n    {\n        BindGridView(0); // Load first page data initially\n    }\n}\n\nprotected void GridView_PageIndexChanging(object sender, GridViewPageEventArgs e)\n{\n    GridView1.PageIndex = e.NewPageIndex;\n    BindGridView(e.NewPageIndex);\n}\n\nprivate void BindGridView(int pageIndex)\n{\n    int pageSize = 10; // Number of records per page\n    int startRowIndex = pageIndex * pageSize;\n\n    // Assuming GetData is a method that fetches data from the database\n    var data = GetData(startRowIndex, pageSize);\n\n    GridView1.DataSource = data;\n    GridView1.DataBind();\n}\n\nprivate DataTable GetData(int startRowIndex, int pageSize)\n{\n    // Use a stored procedure or a query to fetch only the required rows\n    using (SqlConnection conn = new SqlConnection("your_connection_string"))\n    {\n        using (SqlCommand cmd = new SqlCommand("GetPagedData", conn))\n        {\n            cmd.CommandType = CommandType.StoredProcedure;\n            cmd.Parameters.AddWithValue("@StartRowIndex", startRowIndex);\n            cmd.Parameters.AddWithValue("@PageSize", pageSize);\n\n            SqlDataAdapter da = new SqlDataAdapter(cmd);\n            DataTable dt = new DataTable();\n            da.Fill(dt);\n            return dt;\n        }\n    }\n}