using System;\nusing System.IO;\nusing System.Xml;\nusing System.Xml.Xsl;\nusing System.Xml.XPath;\nusing HtmlAgilityPack;\n\npublic class TransformXML\n{\n    public static void Main(string[] args)\n    {\n        try\n        {\n            string sourceDoc = "C:\\CDA_Doc.xml";\n            string resultDoc = "C:\\Result.html";\n            string xsltDoc = "C:\\CDA.xsl";\n\n            XPathDocument myXPathDocument = new XPathDocument(sourceDoc);\n            XslCompiledTransform myXslTransform = new XslCompiledTransform();\n\n            using (XmlTextWriter writer = new XmlTextWriter(resultDoc, null))\n            {\n                myXslTransform.Load(xsltDoc);\n                myXslTransform.Transform(myXPathDocument, null, writer);\n            }\n\n            string htmlContent;\n            using (StreamReader stream = new StreamReader(resultDoc))\n            {\n                htmlContent = stream.ReadToEnd();\n            }\n\n            HtmlDocument doc = new HtmlDocument();\n            doc.LoadHtml(htmlContent);\n            string plainText = doc.DocumentNode.InnerText;\n\n            // Now, plainText contains the rendered text devoid of any markup\n            // You can further process plainText to fit it into 80 character lines if needed\n            Console.WriteLine(plainText);\n\n        }\n        catch (Exception e)\n        {\n            Console.WriteLine("Exception: {0}", e.ToString());\n        }\n    }\n}