using System;\nusing System.Runtime.InteropServices;\n\nclass Program\n{\n    [DllImport("apcups.dll", EntryPoint = "UPSInit")]\n    public static extern int UPSInit();\n\n    [DllImport("apcups.dll", EntryPoint = "UPSTurnOff")]\n    public static extern int UPSTurnOff();\n\n    static void Main()\n    {\n        int initResult = UPSInit();\n        if (initResult == 0) // Assuming 0 is success\n        {\n            int turnOffResult = UPSTurnOff();\n            if (turnOffResult == 0) // Assuming 0 is success\n            {\n                Console.WriteLine("UPS turned off successfully.");\n            }\n            else\n            {\n                Console.WriteLine("Failed to turn off UPS.");\n            }\n        }\n        else\n        {\n            Console.WriteLine("Failed to initialize UPS.");\n        }\n    }\n}