[Test]\npublic void UpdateProduct_ShouldChangeProductDetails()\n{\n    // Arrange\n    var productDal = new ProductDAL();\n    var newProduct = new Product { Name = "Update Test Product" };\n    int newProductId = productDal.Create(newProduct);\n\n    // Act\n    newProduct.Name = "Updated Product Name";\n    productDal.Update(newProduct);\n    var updatedProduct = productDal.Read(newProductId);\n\n    // Assert\n    Assert.AreEqual("Updated Product Name", updatedProduct.Name);\n\n    // Cleanup\n    productDal.Delete(newProductId);\n}