using System.Net;\nusing System.Net.Mail;\n\npublic void SendEmail(string from, string to, string subject, string body, string host, int port, string username, string password)\n{\n    MailMessage msg = new MailMessage();\n    msg.From = new MailAddress(from);\n\n    foreach (string strTo in to.Split(';'))\n    {\n        if (!string.IsNullOrWhiteSpace(strTo))\n        {\n            msg.To.Add(new MailAddress(strTo.Trim()));\n        }\n    }\n\n    msg.Subject = subject;\n    msg.Body = body;\n\n    SmtpClient sm = new SmtpClient(host, port)\n    {\n        DeliveryMethod = SmtpDeliveryMethod.Network,\n        Credentials = new NetworkCredential(username, password)\n    };\n\n    try\n    {\n        sm.Send(msg);\n        Console.WriteLine("Email sent successfully!");\n    }\n    catch (Exception ex)\n    {\n        Console.WriteLine("Failed to send email: " + ex.Message);\n    }\n}