using System;\nusing System.Reflection;\nusing System.EnterpriseServices.Internal;\n\npublic class Program\n{\n    public static void Main()\n    {\n        string assemblyName = "YourAssemblyName, Version=1.0.0.0, Culture=neutral, PublicKeyToken=yourPublicKeyToken";\n        string assemblyPath = GetAssemblyPathFromGAC(assemblyName);\n        \n        if (!string.IsNullOrEmpty(assemblyPath))\n        {\n            Console.WriteLine("Assembly Path: " + assemblyPath);\n        }\n        else\n        {\n            Console.WriteLine("Assembly not found in GAC.");\n        }\n    }\n\n    public static string GetAssemblyPathFromGAC(string assemblyName)\n    {\n        try\n        {\n            Publish publish = new Publish();\n            string gacPath = publish.GacPath(assemblyName);\n            return gacPath;\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine("Error: " + ex.Message);\n            return null;\n        }\n    }\n}