using System;\nusing OpenQA.Selenium;\nusing OpenQA.Selenium.Chrome;\n\npublic class WebAutomation\n{\n    public static void Main(string[] args)\n    {\n        IWebDriver driver = new ChromeDriver();\n        driver.Navigate().GoToUrl("http://example.com");\n\n        // Example: Click a link\n        IWebElement link = driver.FindElement(By.LinkText("Some link text"));\n        link.Click();\n\n        // Example: Fill and submit a form\n        IWebElement inputElement = driver.FindElement(By.Name("inputFieldName"));\n        inputElement.SendKeys("Some text");\n        IWebElement submitButton = driver.FindElement(By.Name("submitButtonName"));\n        submitButton.Click();\n\n        driver.Quit();\n    }\n}