// Using LiteDB\n   using (var db = new LiteDatabase(@"patients.db"))\n   {\n       var patientsCollection = db.GetCollection<Patient>("patients");\n       \n       patientsCollection.Insert(new Patient { FirstName = "John", LastName = "Doe" });\n       \n       var searchResult = patientsCollection.Find(x => x.FirstName.Contains("John") || x.LastName.Contains("Doe")).ToList();\n   }