using System;\nusing System.Data;\nusing System.Web.UI.WebControls;\n\npublic DataTable ExtractGridViewData(GridView gridView)\n{\n    DataTable dt = new DataTable();\n\n    // Add columns to DataTable\n    foreach (DataControlField column in gridView.Columns)\n    {\n        if (column is TemplateField)\n        {\n            TemplateField templateField = column as TemplateField;\n            dt.Columns.Add(templateField.HeaderText);\n        }\n    }\n\n    // Add rows to DataTable\n    foreach (GridViewRow row in gridView.Rows)\n    {\n        DataRow dr = dt.NewRow();\n        for (int i = 0; i < gridView.Columns.Count; i++)\n        {\n            if (gridView.Columns[i] is TemplateField)\n            {\n                TemplateField templateField = gridView.Columns[i] as TemplateField;\n                Label lbl = row.FindControl("lbl" + templateField.HeaderText) as Label;\n                if (lbl != null)\n                {\n                    dr[i] = lbl.Text;\n                }\n            }\n        }\n        dt.Rows.Add(dr);\n    }\n\n    return dt;\n}\n\npublic void DeleteRowFromGridView(GridView gridView, int rowIndex)\n{\n    DataTable dt = ExtractGridViewData(gridView);\n\n    if (rowIndex >= 0 && rowIndex < dt.Rows.Count)\n    {\n        dt.Rows.RemoveAt(rowIndex);\n    }\n\n    gridView.DataSource = dt;\n    gridView.DataBind();\n}