protected void Page_Load(object sender, EventArgs e)\n{\n    // Fetch advertisement data from database\n    var ads = GetAdvertisementsFromDatabase();\n\n    foreach (var ad in ads)\n    {\n        string cssClass = "single-unit";\n        if (ad.Type == "horizontal-2-unit")\n        {\n            cssClass = "horizontal-2-unit";\n        }\n        else if (ad.Type == "vertical-2-unit")\n        {\n            cssClass = "vertical-2-unit";\n        }\n\n        // Dynamically create ad div\n        var adDiv = new HtmlGenericControl("div");\n        adDiv.Attributes["class"] = "ad-unit " + cssClass;\n        adDiv.InnerText = ad.Content; // Assuming ad content is a simple text for this example\n\n        adContainer.Controls.Add(adDiv); // adContainer is the server-side ID of the ad-container div\n    }\n}