[Test]\npublic void ReadProduct_ShouldReturnCorrectProduct()\n{\n    // Arrange\n    var productDal = new ProductDAL();\n    var newProduct = new Product { Name = "Read Test Product" };\n    int newProductId = productDal.Create(newProduct);\n\n    // Act\n    var product = productDal.Read(newProductId);\n\n    // Assert\n    Assert.IsNotNull(product);\n    Assert.AreEqual("Read Test Product", product.Name);\n\n    // Cleanup\n    productDal.Delete(newProductId);\n}