using System;\nusing System.Xml;\n\nclass Program\n{\n    static void Main()\n    {\n        string configPath = @"C:\Users\YourUsername\Desktop\web.config";\n\n        XmlDocument doc = new XmlDocument();\n        doc.Load(configPath);\n\n        XmlNode appSettingsNode = doc.SelectSingleNode("configuration/appSettings");\n\n        if (appSettingsNode != null)\n        {\n            foreach (XmlNode node in appSettingsNode.ChildNodes)\n            {\n                if (node.Attributes["key"].Value == "SomeKey")\n                {\n                    node.Attributes["value"].Value = "NewValue";\n                }\n            }\n        }\n\n        doc.Save(configPath);\n        Console.WriteLine("web.config updated successfully.");\n    }\n}