[Test]\npublic void DeleteProduct_ShouldRemoveProduct()\n{\n    // Arrange\n    var productDal = new ProductDAL();\n    var newProduct = new Product { Name = "Delete Test Product" };\n    int newProductId = productDal.Create(newProduct);\n\n    // Act\n    productDal.Delete(newProductId);\n    var deletedProduct = productDal.Read(newProductId);\n\n    // Assert\n    Assert.IsNull(deletedProduct);\n}