// Create the XmlDocument object\nSystem.Xml.XmlDocument xmlDoc = new System.Xml.XmlDocument();\nxmlDoc.LoadXml(MessagePart); // Assuming MessagePart is your incoming XML message\n\n// Create the NamespaceManager and add the default namespace\nSystem.Xml.XmlNamespaceManager nsmgr = new System.Xml.XmlNamespaceManager(xmlDoc.NameTable);\nnsmgr.AddNamespace("ns", "http://www.aniteps.com/xml/schemas/awm/images4");\n\n// Use the namespace prefix to select the nodes\nstring hasErrors = xmlDoc.SelectSingleNode("//ns:HasErrors", nsmgr).InnerText;\nstring numberOfErrors = xmlDoc.SelectSingleNode("//ns:NumberOfErrors", nsmgr).InnerText;\n\n// Assign the values to your BizTalk variables\nHasErrors = hasErrors;\nNumberOfErrors = numberOfErrors;