using System;\nusing System.Xml;\nusing System.Xml.XPath;\n\npublic class Program\n{\n    public static void Main()\n    {\n        string xml = @"<Elements name=""Entities"" xmlns=""XS-GenerationToolElements""></Elements>";\n\n        XmlDocument doc = new XmlDocument();\n        doc.LoadXml(xml);\n\n        XPathNavigator navigator = doc.CreateNavigator();\n        XmlNamespaceManager namespaceManager = new XmlNamespaceManager(navigator.NameTable);\n\n        // Retrieve the namespace\n        string defaultNamespace = navigator.LookupNamespace(String.Empty);\n        namespaceManager.AddNamespace("ns", defaultNamespace);\n\n        // Use the namespace in XPath queries\n        XPathExpression expr = navigator.Compile("//ns:Elements");\n        expr.SetContext(namespaceManager);\n\n        XPathNodeIterator nodes = navigator.Select(expr);\n        while (nodes.MoveNext())\n        {\n            Console.WriteLine(nodes.Current.OuterXml);\n        }\n    }\n}