using System;\nusing System.Data;\nusing System.Data.SqlClient;\nusing System.Web.Services;\n\npublic class VendorService : WebService\n{\n    [WebMethod]\n    public DataSet GetVendors(string database)\n    {\n        string connString = GetConnString(database);\n\n        using (SqlConnection sqlConn = new SqlConnection(connString))\n        {\n            string strSQL = @"SELECT [No_] AS [VendorNo], \n                                     LTRIM([Name]) AS [VendorName]\n                              FROM [dbo].[$Vendor]\n                              WHERE LEN(RTRIM([Name])) > 0\n                              AND [Vendor Posting Group] = 'VEND'\n                              ORDER BY LTRIM([Name]) ASC;";\n\n            using (SqlDataAdapter da = new SqlDataAdapter(strSQL, sqlConn))\n            {\n                DataSet ds = new DataSet();\n                da.Fill(ds, "Vendors");\n                return ds;\n            }\n        }\n    }\n\n    private string GetConnString(string database)\n    {\n        // Implement this method to securely fetch the connection string for the provided database\n        // Ensure that sensitive information is protected\n        return $"YourConnectionString;Database={database};";\n    }\n}