using Aspose.Email;\nusing Aspose.Email.Tools;\nusing Aspose.Words;\nusing System.IO;\n\npublic class EmlToPdfConverter\n{\n    public static void ConvertEmlToPdf(string emlFilePath, string pdfFilePath)\n    {\n        // Load the .eml file\n        MailMessage eml = MailMessage.Load(emlFilePath);\n\n        // Convert .eml to .mhtml\n        MemoryStream mhtmlStream = new MemoryStream();\n        eml.Save(mhtmlStream, SaveOptions.DefaultMhtml);\n        mhtmlStream.Position = 0;\n\n        // Load the .mhtml into a Aspose.Words Document\n        Document doc = new Document(mhtmlStream);\n\n        // Save the document as a PDF\n        doc.Save(pdfFilePath, SaveFormat.Pdf);\n    }\n}\n\n// Usage example\nclass Program\n{\n    static void Main()\n    {\n        string emlFilePath = "path/to/your/email.eml";\n        string pdfFilePath = "path/to/your/output.pdf";\n\n        EmlToPdfConverter.ConvertEmlToPdf(emlFilePath, pdfFilePath);\n    }\n}