using System;\nusing System.IO;\n\npublic class UserPreferences\n{\n    private static readonly string PreferencesFolderPath = Path.Combine(\n        Environment.GetFolderPath(Environment.SpecialFolder.LocalApplicationData), "YourAppName");\n\n    private static readonly string PreferencesFilePath = Path.Combine(PreferencesFolderPath, "userPreferences.json");\n\n    public static void SavePreferences(object preferences)\n    {\n        if (!Directory.Exists(PreferencesFolderPath))\n        {\n            Directory.CreateDirectory(PreferencesFolderPath);\n        }\n\n        string json = Newtonsoft.Json.JsonConvert.SerializeObject(preferences);\n        File.WriteAllText(PreferencesFilePath, json);\n    }\n\n    public static T LoadPreferences<T>() where T : new()\n    {\n        if (File.Exists(PreferencesFilePath))\n        {\n            string json = File.ReadAllText(PreferencesFilePath);\n            return Newtonsoft.Json.JsonConvert.DeserializeObject<T>(json);\n        }\n\n        return new T();\n    }\n}