using System;\nusing System.Security.Cryptography.X509Certificates;\n\nclass Program\n{\n    static void Main()\n    {\n        string[] certificatePaths = { @"path\to\cert1.pfx", @"path\to\cert2.pfx", /* more paths */ };\n        string[] certificatePasswords = { "password1", "password2", /* more passwords */ };\n\n        try\n        {\n            X509Store store = new X509Store(StoreName.My, StoreLocation.CurrentUser);\n            store.Open(OpenFlags.ReadWrite);\n\n            for (int i = 0; i < certificatePaths.Length; i++)\n            {\n                // Load the certificate from file\n                X509Certificate2 certificate = new X509Certificate2(certificatePaths[i], certificatePasswords[i]);\n\n                // Add the certificate to the store\n                store.Add(certificate);\n            }\n\n            // Close the store\n            store.Close();\n\n            Console.WriteLine("All certificates installed successfully.");\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine($"An error occurred: {ex.Message}");\n        }\n    }\n}