using System;\nusing System.Collections.Generic;\nusing System.Data;\nusing System.Data.SqlClient;\n\npublic static class CustomerMapper\n{\n    private static string connectionString = "your_connection_string_here";\n\n    public static Customer MapToCustomer(IDataRecord record)\n    {\n        return new Customer\n        {\n            Id = Convert.ToInt32(record["Id"]),\n            FirstName = record["FirstName"].ToString(),\n            LastName = record["LastName"].ToString(),\n            Email = record["Email"].ToString()\n        };\n    }\n\n    public static List<Customer> GetAllCustomers()\n    {\n        List<Customer> customers = new List<Customer>();\n\n        using (SqlConnection conn = new SqlConnection(connectionString))\n        {\n            SqlCommand cmd = new SqlCommand("GetAllCustomers", conn);\n            cmd.CommandType = CommandType.StoredProcedure;\n\n            conn.Open();\n            using (SqlDataReader reader = cmd.ExecuteReader())\n            {\n                while (reader.Read())\n                {\n                    customers.Add(MapToCustomer(reader));\n                }\n            }\n        }\n\n        return customers;\n    }\n\n    public static Customer GetCustomerById(int id)\n    {\n        Customer customer = null;\n\n        using (SqlConnection conn = new SqlConnection(connectionString))\n        {\n            SqlCommand cmd = new SqlCommand("GetCustomerById", conn);\n            cmd.CommandType = CommandType.StoredProcedure;\n            cmd.Parameters.AddWithValue("@Id", id);\n\n            conn.Open();\n            using (SqlDataReader reader = cmd.ExecuteReader())\n            {\n                if (reader.Read())\n                {\n                    customer = MapToCustomer(reader);\n                }\n            }\n        }\n\n        return customer;\n    }\n\n    public static void InsertCustomer(Customer customer)\n    {\n        using (SqlConnection conn = new SqlConnection(connectionString))\n        {\n            SqlCommand cmd = new SqlCommand("InsertCustomer", conn);\n            cmd.CommandType = CommandType.StoredProcedure;\n            cmd.Parameters.AddWithValue("@FirstName", customer.FirstName);\n            cmd.Parameters.AddWithValue("@LastName", customer.LastName);\n            cmd.Parameters.AddWithValue("@Email", customer.Email);\n\n            conn.Open();\n            cmd.ExecuteNonQuery();\n        }\n    }\n\n    public static void UpdateCustomer(Customer customer)\n    {\n        using (SqlConnection conn = new SqlConnection(connectionString))\n        {\n            SqlCommand cmd = new SqlCommand("UpdateCustomer", conn);\n            cmd.CommandType = CommandType.StoredProcedure;\n            cmd.Parameters.AddWithValue("@Id", customer.Id);\n            cmd.Parameters.AddWithValue("@FirstName", customer.FirstName);\n            cmd.Parameters.AddWithValue("@LastName", customer.LastName);\n            cmd.Parameters.AddWithValue("@Email", customer.Email);\n\n            conn.Open();\n            cmd.ExecuteNonQuery();\n        }\n    }\n\n    public static void DeleteCustomer(int id)\n    {\n        using (SqlConnection conn = new SqlConnection(connectionString))\n        {\n            SqlCommand cmd = new SqlCommand("DeleteCustomer", conn);\n            cmd.CommandType = CommandType.StoredProcedure;\n            cmd.Parameters.AddWithValue("@Id", id);\n\n            conn.Open();\n            cmd.ExecuteNonQuery();\n        }\n    }\n}