using System;\nusing System.Linq;\nusing System.Data.Linq;\nusing System.Data.Linq.Mapping;\n\npublic class Post\n{\n    public int PostId { get; set; }\n    public DateTime PostedDateTimeUtc { get; set; }\n}\n\npublic class MyDataContext : DataContext\n{\n    public Table<Post> Posts;\n\n    public MyDataContext(string connectionString) : base(connectionString) { }\n}\n\npublic class Program\n{\n    public static void Main()\n    {\n        string connectionString = "your_connection_string_here";\n        using (var context = new MyDataContext(connectionString))\n        {\n            var query = "select distinct top 1 'PostId' = isnull(RootPost, Id), PostedDateTimeUtc from Post order by PostedDateTimeUtc desc";\n            \n            var results = context.ExecuteQuery<dynamic>(query).Select(result => new\n            {\n                PostId = result.PostId,\n                PostedDateTimeUtc = result.PostedDateTimeUtc\n            }).ToList();\n\n            foreach (var result in results)\n            {\n                Console.WriteLine($"PostId: {result.PostId}, PostedDateTimeUtc: {result.PostedDateTimeUtc}");\n            }\n        }\n    }\n}