using System.Web.Services;\n\n[WebService(Namespace = "http://example.com/")]\n[WebServiceBinding(ConformsTo = WsiProfiles.BasicProfile1_1)]\npublic class MyWebService : WebService\n{\n    [WebMethod]\n    public string GetMethod()\n    {\n        if (Context.Request.HttpMethod != "GET")\n        {\n            Context.Response.StatusCode = 405; // Method Not Allowed\n            return "This method only supports GET requests.";\n        }\n\n        // Your logic for GET method\n        return "This is a GET response.";\n    }\n\n    [WebMethod]\n    public string PostMethod()\n    {\n        if (Context.Request.HttpMethod != "POST")\n        {\n            Context.Response.StatusCode = 405; // Method Not Allowed\n            return "This method only supports POST requests.";\n        }\n\n        // Your logic for POST method\n        return "This is a POST response.";\n    }\n}