using System;\nusing System.IO;\nusing iTextSharp.text;\nusing iTextSharp.text.pdf;\nusing iTextSharp.tool.xml;\n\npublic class Program\n{\n    public static void Main()\n    {\n        string htmlContent = @"\n            <html>\n                <head>\n                    <style>\n                        body { font-family: Arial, sans-serif; }\n                        .header { background-color: #f2f2f2; padding: 10px; text-align: center; }\n                        .content { margin: 20px; }\n                    </style>\n                </head>\n                <body>\n                    <div class='header'>\n                        <h1>Sample PDF</h1>\n                    </div>\n                    <div class='content'>\n                        <p>This is a sample PDF created using iTextSharp with HTML and CSS.</p>\n                    </div>\n                </body>\n            </html>";\n\n        using (var ms = new MemoryStream())\n        {\n            using (var document = new Document())\n            {\n                PdfWriter writer = PdfWriter.GetInstance(document, ms);\n                document.Open();\n\n                using (var cssStream = new MemoryStream(System.Text.Encoding.UTF8.GetBytes(htmlContent)))\n                {\n                    XMLWorkerHelper.GetInstance().ParseXHtml(writer, document, cssStream, cssStream);\n                }\n\n                document.Close();\n            }\n\n            File.WriteAllBytes("output.pdf", ms.ToArray());\n        }\n    }\n}