using System;\nusing System.Reflection;\n\nclass Program\n{\n    static void Main()\n    {\n        // Ensure you have ReflectionPermission\n        ReflectionPermission reflectionPerm = new ReflectionPermission(PermissionState.Unrestricted);\n        reflectionPerm.Demand();\n\n        // Load the assembly\n        Assembly asm = Assembly.LoadFrom("Chapter13.exe");\n        \n        // Get the type\n        Type t = asm.GetType("Chapter13.ProtectedBuffer");\n        if (t == null)\n        {\n            Console.WriteLine("Type not found.");\n            return;\n        }\n\n        // Get all members including non-public ones\n        MemberInfo[] members = t.GetMembers(BindingFlags.NonPublic | BindingFlags.Instance | BindingFlags.Static);\n\n        // Display the member names\n        foreach (MemberInfo m in members)\n        {\n            Console.WriteLine(m.Name);\n        }\n    }\n}