public class ContactService\n{\n    private readonly IContactRepository _contactRepository;\n    private readonly IUnitOfWork _unitOfWork;\n\n    public ContactService(IContactRepository contactRepository, IUnitOfWork unitOfWork)\n    {\n        _contactRepository = contactRepository;\n        _unitOfWork = unitOfWork;\n    }\n\n    public async Task DeleteContactAsync(int contactId, DateTime contactTimestamp)\n    {\n        _contactRepository.Delete(contactId, contactTimestamp);\n        await _unitOfWork.CompleteAsync();\n    }\n}