using System;\nusing System.DirectoryServices;\n\nclass Program\n{\n    static void Main(string[] args)\n    {\n        string serverName = "RemoteServerName"; // Replace with your remote server name\n        string comPlusApplicationName = "YourCOMPlusApplicationName"; // Replace with your COM+ application name\n\n        try\n        {\n            string path = $"WinNT://{serverName}/LM/COM+ Applications";\n            using (DirectoryEntry directoryEntry = new DirectoryEntry(path))\n            using (DirectoryEntry comPlusApp = directoryEntry.Children.Find(comPlusApplicationName, "Application"))\n            {\n                // Stop the application\n                comPlusApp.Invoke("Stop", null);\n                Console.WriteLine("COM+ application stopped.");\n\n                // Start the application\n                comPlusApp.Invoke("Start", null);\n                Console.WriteLine("COM+ application started.");\n            }\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine($"An error occurred: {ex.Message}");\n        }\n    }\n}