protected void SaveFieldState(string fieldId, bool isCompleted)\n{\n    Dictionary<string, bool> requiredFieldStates = (Dictionary<string, bool>)Session["RequiredFieldStates"];\n    if (requiredFieldStates == null)\n    {\n        requiredFieldStates = new Dictionary<string, bool>();\n    }\n    requiredFieldStates[fieldId] = isCompleted;\n    Session["RequiredFieldStates"] = requiredFieldStates;\n}