using System;\nusing System.Data.SqlClient;\nusing System.Transactions;\n\npublic class TestDatabaseTransaction\n{\n    public void ExecuteTestWithTransaction()\n    {\n        // Connection strings to your database\n        string connectionString1 = "your_connection_string_1";\n        string connectionString2 = "your_connection_string_2";\n\n        using (TransactionScope scope = new TransactionScope())\n        {\n            using (SqlConnection connection1 = new SqlConnection(connectionString1))\n            {\n                connection1.Open();\n                // Perform your database operations on connection1\n                // Example: Insert, Update, Delete operations\n\n                using (SqlConnection connection2 = new SqlConnection(connectionString2))\n                {\n                    connection2.Open();\n                    // Perform your database operations on connection2\n                    // Example: Insert, Update, Delete operations\n\n                    // Commit the transaction\n                    scope.Complete();\n                }\n            }\n        }\n    }\n}