protected void Timer1_Tick(object sender, EventArgs e)\n{\n    // Check if the session variable exists\n    if (Session["ElapsedTime"] == null)\n    {\n        // Initialize the session variable if it does not exist\n        Session["ElapsedTime"] = 0;\n    }\n\n    // Increment the elapsed time\n    int elapsedTime = (int)Session["ElapsedTime"];\n    elapsedTime += Timer1.Interval / 1000; // Timer1.Interval is in milliseconds, convert to seconds\n    Session["ElapsedTime"] = elapsedTime;\n\n    // Check if the elapsed time exceeds the desired threshold\n    if (elapsedTime >= 60) // Example: stop the timer after 60 seconds\n    {\n        Timer1.Enabled = false; // Disable the timer\n        // Perform additional actions if needed\n    }\n}