using System.Web.Mvc;\nusing System.Web.Routing;\n\npublic class RouteConfig\n{\n    public static void RegisterRoutes(RouteCollection routes)\n    {\n        routes.IgnoreRoute("{resource}.axd/{*pathInfo}");\n\n        routes.MapRoute(\n            name: "UserBookmarks",\n            url: "users/{username}/bookmarks",\n            defaults: new { controller = "Bookmarks", action = "List" }\n        );\n\n        routes.MapRoute(\n            name: "UserBookmark",\n            url: "users/{username}/bookmark/{bookmarkid}",\n            defaults: new { controller = "Bookmarks", action = "Update" },\n            constraints: new { bookmarkid = @"\d+" } // Ensures bookmarkid is a number\n        );\n\n        routes.MapRoute(\n            name: "Default",\n            url: "{controller}/{action}/{id}",\n            defaults: new { controller = "Home", action = "Index", id = UrlParameter.Optional }\n        );\n    }\n}