using HtmlAgilityPack;\nusing System;\nusing System.Collections.Generic;\n\nclass Program\n{\n    static void Main()\n    {\n        string html = "<html><body><a href='http://example.com'>Example</a><a href='http://another.com'>Another</a></body></html>";\n\n        HtmlDocument document = new HtmlDocument();\n        document.LoadHtml(html);\n\n        List<string> hrefValues = new List<string>();\n\n        foreach (HtmlNode link in document.DocumentNode.SelectNodes("//a[@href]"))\n        {\n            HtmlAttribute attr = link.Attributes["href"];\n            hrefValues.Add(attr.Value);\n        }\n\n        foreach (string href in hrefValues)\n        {\n            Console.WriteLine(href);\n        }\n    }\n}