using System;\nusing System.Text.RegularExpressions;\n\nclass Program\n{\n    static void Main()\n    {\n        string input = "The big <bpt i=\"1\" x=\"1\" type=\"bold\"><b></bpt>black<ept i=\"1\"></b></ept> <bpt i=\"2\" x=\"2\" type=\"ulined\"><u></bpt>cat<ept i=\"2\"></u></ept> sleeps.\n";\n        string pattern = @"<bpt[^>]*>.*?</bpt>|<ept[^>]*>.*?</ept>";\n        string result = Regex.Replace(input, pattern, string.Empty, RegexOptions.Singleline);\n        \n        Console.WriteLine(result);\n    }\n}