using Microsoft.AspNetCore.Mvc;\nusing Twilio.AspNet.Common;\nusing Twilio.AspNet.Core;\nusing Twilio.TwiML;\n\nnamespace SmsReceiver.Controllers\n{\n    [Route("api/[controller]")]\n    [ApiController]\n    public class SmsController : TwilioController\n    {\n        [HttpPost]\n        public IActionResult ReceiveSms(SmsRequest incomingMessage)\n        {\n            // Log the incoming message or process it as needed\n            var from = incomingMessage.From;\n            var body = incomingMessage.Body;\n\n            // Create a response message (optional)\n            var response = new MessagingResponse();\n            response.Message("Thanks for your message!");\n\n            return TwiML(response);\n        }\n    }\n}