using System;\nusing System.Collections.Generic;\n\npublic class Employee\n{\n    public string Id { get; set; }\n    public string Name { get; set; }\n}\n\npublic class Department\n{\n    public string Id { get; set; }\n    public List<Employee> Employees { get; set; } = new List<Employee>();\n}\n\npublic class Organization\n{\n    private Dictionary<string, Department> departments = new Dictionary<string, Department>();\n\n    public void AddDepartment(string departmentId)\n    {\n        if (!departments.ContainsKey(departmentId))\n        {\n            departments[departmentId] = new Department { Id = departmentId };\n        }\n    }\n\n    public void AddEmployee(string departmentId, string employeeId, string employeeName)\n    {\n        if (!departments.ContainsKey(departmentId))\n        {\n            departments[departmentId] = new Department { Id = departmentId };\n        }\n\n        departments[departmentId].Employees.Add(new Employee { Id = employeeId, Name = employeeName });\n    }\n\n    public Dictionary<string, Department> GetDepartments()\n    {\n        return departments;\n    }\n}\n\n// Example usage\npublic class Program\n{\n    public static void Main()\n    {\n        Organization org = new Organization();\n\n        org.AddDepartment("Accounting");\n        org.AddEmployee("Accounting", "1234", "Joe Jones");\n        org.AddEmployee("Accounting", "5678", "Steve Smith");\n\n        org.AddDepartment("Marketing");\n        org.AddEmployee("Marketing", "3223", "Kate Connors");\n        org.AddEmployee("Marketing", "3218", "Noble Washington");\n        org.AddEmployee("Marketing", "3233", "James Thomas");\n\n        var departments = org.GetDepartments();\n\n        foreach (var dept in departments.Values)\n        {\n            Console.WriteLine($"Department: {dept.Id}");\n            foreach (var emp in dept.Employees)\n            {\n                Console.WriteLine($"  Employee ID: {emp.Id}, Name: {emp.Name}");\n            }\n        }\n    }\n}