IAzClientContext2 CreateClientContext(IAzApplication2 app, SecurityIdentifier userSid)\n{\n    // Initialize a new client context instance\n    IAzClientContext2 cctx = app.InitializeClientContext2("AppNameHere", null);\n    \n    // Add the SID of the current user to the context\n    cctx.AddStringSids(new object[] { (object)userSid.ToString() } as object);\n    \n    // Return the freshly created client context\n    return cctx;\n}\n\n// Example usage:\nIAzClientContext2 userContext = CreateClientContext(app, ClientContext.SID);