public void ProcessRequest(HttpContext context)\n{\n    string sessionId = context.Request.Form["SessionID"];\n    if (!string.IsNullOrEmpty(sessionId))\n    {\n        HttpSessionState session = SessionHelper.GetSession(sessionId);\n        if (session != null)\n        {\n            string userName = session["UserName"] as string;\n            if (!string.IsNullOrEmpty(userName))\n            {\n                // Now you have the user name\n                context.Response.Write("User Name: " + userName);\n            }\n            else\n            {\n                context.Response.Write("No user name found in session.");\n            }\n        }\n        else\n        {\n            context.Response.Write("Session not found.");\n        }\n    }\n    else\n    {\n        context.Response.Write("Session ID is required.");\n    }\n}