using System;\nusing System.Web;\nusing System.Web.SessionState;\n\npublic class SessionHelper\n{\n    public static HttpSessionState GetSession(string sessionId)\n    {\n        // Use the session state provider to get the session data\n        var sessionContainer = new HttpSessionStateContainer(sessionId, new SessionStateItemCollection(),\n            HttpContext.Current.Session.StaticObjects, 10, true, HttpCookieMode.AutoDetect,\n            SessionStateMode.StateServer, false);\n        \n        return new HttpSessionState(sessionContainer);\n    }\n}