using System;\nusing System.Web.UI;\n\npublic class SpiffyPage : Page\n{\n    protected override void OnLoad(EventArgs e)\n    {\n        base.OnLoad(e);\n        \n        // Load the SpiffyControl user control\n        SpiffyControl spiffyControl = (SpiffyControl)LoadControl("~/Controls/SpiffyControl.ascx");\n        \n        // Set properties on the SpiffyControl\n        spiffyControl.SomeProperty = "SomeValue";\n        \n        // Add the control to a placeholder or control collection on the page\n        this.Controls.Add(spiffyControl);\n    }\n}