using System;\nusing System.Diagnostics;\nusing System.Threading.Tasks;\n\nclass Program\n{\n    static async Task Main(string[] args)\n    {\n        await RunBatchFileAsync("path_to_your_batch_file.bat");\n    }\n\n    static async Task RunBatchFileAsync(string fileName)\n    {\n        using (Process proc = new Process())\n        {\n            proc.StartInfo.FileName = fileName;\n            proc.StartInfo.WindowStyle = ProcessWindowStyle.Hidden;\n            proc.StartInfo.CreateNoWindow = true;\n            proc.StartInfo.RedirectStandardOutput = true;\n            proc.StartInfo.RedirectStandardError = true;\n\n            proc.OutputDataReceived += (sender, args) => Console.WriteLine(args.Data);\n            proc.ErrorDataReceived += (sender, args) => Console.WriteLine(args.Data);\n\n            proc.Start();\n\n            proc.BeginOutputReadLine();\n            proc.BeginErrorReadLine();\n\n            await Task.Run(() => proc.WaitForExit());\n        }\n    }\n}