public async Task LoadProducts(int pageNumber, int pageSize)\n{\n    var productService = new ProductServiceClient();\n    var result = await productService.GetProductsAsync(pageNumber, pageSize);\n\n    // Populate grid with result.Items\n    foreach (var product in result.Items)\n    {\n        // Optionally, load additional details asynchronously\n        var detail = await productService.GetProductDetailAsync(product.Id);\n        // Update grid with product details\n    }\n}