protected void Button1_Click(object sender, EventArgs e)\n{\n    if (TextBox1.Text == "")\n    {\n        Label3.Visible = true;\n        Label3.Text = "* Required Field";\n    }\n    else if (TextBox2.Text == "")\n    {\n        Label4.Visible = true;\n        Label4.Text = "* Required Field";\n    }\n    else\n    {\n        Label3.Visible = false;\n        Label4.Visible = false;\n        string userid = TextBox1.Text;\n        string pass = TextBox2.Text;\n\n        string connectionString = "SERVER= server3\\OFFICESERVERS; Initial catalog = Web; Integrated Security = SSPI";\n        using (SqlConnection conn = new SqlConnection(connectionString))\n        {\n            string query = "SELECT COUNT(*) FROM web WHERE IsActive=1 AND LoginName=@LoginName AND Password=@Password";\n            SqlCommand mycmd = new SqlCommand(query, conn);\n            mycmd.Parameters.AddWithValue("@LoginName", userid);\n            mycmd.Parameters.AddWithValue("@Password", pass);\n\n            try\n            {\n                conn.Open();\n                int userCount = (int)mycmd.ExecuteScalar();\n\n                if (userCount > 0)\n                {\n                    // Login successful, redirect to home page\n                    Response.Redirect("HomePage.aspx");\n                }\n                else\n                {\n                    // Invalid login\n                    Label3.Visible = true;\n                    Label3.Text = "Invalid username or password.";\n                }\n            }\n            catch (Exception ex)\n            {\n                // Handle exception\n                Label3.Visible = true;\n                Label3.Text = "An error occurred: " + ex.Message;\n            }\n        }\n    }\n}