using System;\n\n[AttributeUsage(AttributeTargets.Class | AttributeTargets.Method)]\npublic class SomeAttribute : Attribute\n{\n    private readonly Guid foreignIdentificatorGuid;\n\n    public SomeAttribute(string foreignIdentificator)\n    {\n        if (!Guid.TryParse(foreignIdentificator, out foreignIdentificatorGuid))\n        {\n            throw new ArgumentException("The provided string is not a valid GUID.", nameof(foreignIdentificator));\n        }\n    }\n\n    public Guid ForeignIdentificatorGuid => foreignIdentificatorGuid;\n}