using System;\nusing ADOX; // Add reference to ADOX\n\nclass Program\n{\n    static void Main()\n    {\n        string connectionString = @"Provider=Microsoft.Jet.OLEDB.4.0;Data Source=C:\Path\To\Your\Database.mdb;";\n        Catalog catalog = new Catalog();\n        catalog.ActiveConnection = connectionString;\n\n        foreach (Table table in catalog.Tables)\n        {\n            // Skip system tables\n            if (table.Type == "TABLE")\n            {\n                Console.WriteLine("Table: " + table.Name);\n\n                foreach (Key key in table.Keys)\n                {\n                    if (key.Type == KeyTypeEnum.adKeyPrimary)\n                    {\n                        Console.WriteLine("Primary Key Constraint Name: " + key.Name);\n                    }\n                }\n            }\n        }\n\n        // Clean up\n        catalog.ActiveConnection = null;\n    }\n}