using System;\nusing mshtml;\nusing SHDocVw;\n\nclass Program\n{\n    static void Main()\n    {\n        // Create an instance of Internet Explorer\n        InternetExplorer ie = new InternetExplorer();\n        ie.Visible = true;\n\n        // Navigate to the desired URL\n        string url = "https://www.yahoo.com";\n        ie.Navigate(url);\n\n        // Wait for the page to load completely\n        while (ie.Busy || ie.ReadyState != tagREADYSTATE.READYSTATE_COMPLETE)\n        {\n            System.Threading.Thread.Sleep(100);\n        }\n\n        // Get the HTML document\n        HTMLDocumentClass doc = (HTMLDocumentClass)ie.Document;\n        \n        // Example of querying by tag name and attribute\n        // This may vary based on the actual structure of the page\n        foreach (IHTMLElement element in doc.getElementsByTagName("button"))\n        {\n            // Check for a specific attribute or text content\n            if (element.innerText == "Web Search")\n            {\n                element.click();\n                break;\n            }\n        }\n\n        // Alternatively, you can use querySelector if supported\n        // IHTMLElement btn = (IHTMLElement)doc.querySelector("button[attribute='value']");\n        // btn.click();\n\n        // Clean up\n        ie.Quit();\n    }\n}