public class Processor\n{\n    public void Execute(Action step1, Action step2)\n    {\n        // Common steps\n        Console.WriteLine("Common Step A");\n\n        // Variable steps\n        step1();\n        step2();\n\n        // Common steps\n        Console.WriteLine("Common Step B");\n    }\n}\n\nclass Program\n{\n    static void Main()\n    {\n        var processor = new Processor();\n        \n        processor.Execute(\n            () => Console.WriteLine("Custom Step 1"),\n            () => Console.WriteLine("Custom Step 2")\n        );\n    }\n}