Uri newUri = new Uri("https://some.random.url:443/random");\nHttpWebRequest wr = (HttpWebRequest)WebRequest.Create(newUri);\n\n// Set HTTP method and content type\nwr.Method = "POST";\nwr.ContentType = "application/x-www-form-urlencoded";\n\n// If you are using a proxy, set it like this:\n// wr.Proxy = new WebProxy("http://yourproxyaddress:port", true);\n\n// Optional: Bypass SSL certificate validation (not recommended for production)\nServicePointManager.ServerCertificateValidationCallback = delegate { return true; };\n\n// Write data to request\nASCIIEncoding encoding = new ASCIIEncoding();\nbyte[] requestBytes = encoding.GetBytes("test");\nwr.ContentLength = requestBytes.Length;\n\nusing (System.IO.Stream stream = wr.GetRequestStream())\n{\n    stream.Write(requestBytes, 0, requestBytes.Length);\n}\n\n// Get response\nusing (HttpWebResponse response = (HttpWebResponse)wr.GetResponse())\n{\n    using (StreamReader reader = new StreamReader(response.GetResponseStream()))\n    {\n        string responseText = reader.ReadToEnd();\n        Console.WriteLine(responseText);\n    }\n}