using System;\nusing System.Runtime.InteropServices;\nusing System.Text;\n\nclass Program\n{\n    const int MF_BYPOSITION = 0x400;\n    const int MF_STRING = 0x0;\n\n    [DllImport("user32.dll", CharSet = CharSet.Auto)]\n    private static extern IntPtr FindWindow(string lpClassName, string lpWindowName);\n\n    [DllImport("user32.dll", CharSet = CharSet.Auto)]\n    private static extern IntPtr GetMenu(IntPtr hWnd);\n\n    [DllImport("user32.dll", CharSet = CharSet.Auto)]\n    private static extern int GetMenuItemCount(IntPtr hMenu);\n\n    [DllImport("user32.dll", CharSet = CharSet.Auto)]\n    private static extern bool InsertMenu(IntPtr hMenu, int wPosition, int wFlags, int wIDNewItem, string lpNewItem);\n\n    [DllImport("user32.dll", CharSet = CharSet.Auto)]\n    private static extern bool DrawMenuBar(IntPtr hWnd);\n\n    static void Main(string[] args)\n    {\n        IntPtr hWnd = FindWindow(null, "Gtalk"); // Replace "Gtalk" with the actual window title\n        if (hWnd != IntPtr.Zero)\n        {\n            IntPtr hMenu = GetMenu(hWnd);\n            if (hMenu != IntPtr.Zero)\n            {\n                int menuItemCount = GetMenuItemCount(hMenu);\n                InsertMenu(hMenu, menuItemCount, MF_BYPOSITION | MF_STRING, 1000, "New Menu Item");\n                DrawMenuBar(hWnd);\n            }\n        }\n        else\n        {\n            Console.WriteLine("Application window not found.");\n        }\n    }\n}