using System;\nusing System.Diagnostics;\n\nclass Program\n{\n    static void Main()\n    {\n        Process currentProcess = Process.GetCurrentProcess();\n\n        // Total memory in use (private bytes)\n        long totalMemoryInUse = currentProcess.PrivateMemorySize64;\n        Console.WriteLine($"Total Memory in Use: {totalMemoryInUse / 1024} KB");\n\n        // Total RAM in use (working set)\n        long totalRamInUse = currentProcess.WorkingSet64;\n        Console.WriteLine($"Total RAM in Use: {totalRamInUse / 1024} KB");\n\n        // Total memory used by managed objects (GC.GetTotalMemory)\n        long totalManagedMemory = GC.GetTotalMemory(false);\n        Console.WriteLine($"Total Managed Memory: {totalManagedMemory / 1024} KB");\n\n        // Total memory usage solely by your program\n        // Note: This is complex and may require extensive profiling and custom instrumentation.\n\n        // Total shared memory usage\n        // Note: This typically requires OS-level tools or APIs to inspect shared memory regions.\n    }\n}