using System;\nusing System.Windows.Forms;\n\npublic class DoubleKeyStrokeForm : Form\n{\n    private bool ctrlPressed = false;\n    private bool firstMPressed = false;\n\n    public DoubleKeyStrokeForm()\n    {\n        this.KeyDown += new KeyEventHandler(OnKeyDown);\n        this.KeyUp += new KeyEventHandler(OnKeyUp);\n    }\n\n    private void OnKeyDown(object sender, KeyEventArgs e)\n    {\n        if (e.Control && e.KeyCode == Keys.M)\n        {\n            if (ctrlPressed && firstMPressed)\n            {\n                // Double keystroke ^MM detected\n                MessageBox.Show("Double keystroke ^MM detected!");\n                firstMPressed = false; // Reset state\n            }\n            else if (ctrlPressed)\n            {\n                firstMPressed = true;\n            }\n        }\n        else if (e.Control)\n        {\n            ctrlPressed = true;\n        }\n    }\n\n    private void OnKeyUp(object sender, KeyEventArgs e)\n    {\n        if (e.KeyCode == Keys.ControlKey)\n        {\n            ctrlPressed = false;\n            firstMPressed = false;\n        }\n        else if (e.KeyCode == Keys.M)\n        {\n            firstMPressed = false;\n        }\n    }\n\n    [STAThread]\n    public static void Main()\n    {\n        Application.EnableVisualStyles();\n        Application.Run(new DoubleKeyStrokeForm());\n    }\n}