using System;\nusing System.IO;\nusing System.Net;\nusing System.Text;\n\npublic class WebPost\n{\n    public string Post(string url, string data)\n    {\n        string result = string.Empty;\n\n        try\n        {\n            byte[] buffer = Encoding.UTF8.GetBytes(data);\n            HttpWebRequest webRequest = (HttpWebRequest)WebRequest.Create(url);\n            webRequest.Method = "POST";\n            webRequest.ContentType = "application/x-www-form-urlencoded";\n            webRequest.ContentLength = buffer.Length;\n\n            using (Stream postData = webRequest.GetRequestStream())\n            {\n                postData.Write(buffer, 0, buffer.Length);\n            }\n\n            using (HttpWebResponse webResponse = (HttpWebResponse)webRequest.GetResponse())\n            {\n                using (Stream answer = webResponse.GetResponseStream())\n                {\n                    using (StreamReader reader = new StreamReader(answer))\n                    {\n                        result = reader.ReadToEnd();\n                    }\n                }\n            }\n        }\n        catch (WebException ex)\n        {\n            using (StreamReader reader = new StreamReader(ex.Response.GetResponseStream()))\n            {\n                string error = reader.ReadToEnd();\n                Console.WriteLine("Error: " + error);\n            }\n        }\n        catch (Exception ex)\n        {\n            Console.WriteLine("Exception: " + ex.Message);\n        }\n\n        return result;\n    }\n}\n\npublic class Program\n{\n    public static void Main()\n    {\n        string verification_url = "http://site.com/posttest.php";\n        string verification_data = "test=524001A";\n        WebPost webPost = new WebPost();\n        string result = webPost.Post(verification_url, verification_data);\n        Console.WriteLine("Server Response: " + result);\n    }\n}